# Example of defining a class
class Person:
   def init(self, name, age):
      self.name = name
      self.age = age
   def greet(self):
      print("Hello, my name is", self.name)
# Example of creating an object
person1 = Person("John", 30)
person1.greet()  # Output: Hello, my name is John
